    private List<Category> categories;
    private Category category = new Category();

    string strError = "";
    bool ShowPopup = false;



    void AddNewUser()
    {
        category = new Category();
        category.Id = 0;
        ShowPopup = true;
    }
    async Task SaveUser()
    {
        try
        {
            // Is this an existing user?
            if (category.Id != 0)
            {
                // Get the user
                Category pos = await db.Categories.FindAsync(category.Id);
                pos.Name = category.Name;
                await db.SaveChangesAsync();
            }
            else
            {
                db.Categories.Add(category);
                await db.SaveChangesAsync();
            }

            // Close the Popup
            ShowPopup = false;
            // Refresh Users
            LoadData();
            category = new Category();
        }
        catch (Exception ex)
        {
            strError = ex.GetBaseException().Message;
        }
    }
    async Task EditUser(Category rc)
    {
        category = rc;
        //products = db.Products.Where(x => x.CategoryId == ).ToList();
        ShowPopup = true;
    }
    async Task DeleteUser()
    {
        // Close the Popup
        ShowPopup = false;
        // Get the user
        var rc = await db.Categories.FindAsync(category.Id);
        if (rc != null)
        {
            db.Categories.Remove(rc);
            await db.SaveChangesAsync();
        }
        LoadData();
    }
    void ClosePopup()
    {
        // Close the Popup
        ShowPopup = false;
    }


    protected override async Task OnInitializedAsync()
    {
        LoadData();
    }

    public void LoadData()
    {
        categories = db.Categories.ToList();
    }

