        set
        {
            CurrentValue = hospitals.Count();
            if (value.Length > 0)
            {
                hospitals = hospitals2.Where(x => x.Name.ToLower().Contains(value)).ToList();
            }
            else
            {
                hospitals = hospitals2;
            }
            itemValue = value;
